// prototype属性的引入
function People () {
    this.shang = 2;
    this.xia = 2;
    this.eye = 2;

    this.say = (name) => {
         console.log('People:', name)
    }

}

let onePeople = new People();

function Man () {

    this.status = () => {
        console.log('eye', this.eye)
    }

}

Man.prototype = onePeople;

let oneMan = new Man();

oneMan.status();
oneMan.say('xiaogang');
使用ES6 class 来定义类
class People {
    constructor() {
        this.shang = 2;
        this.xia = 2;
        this.eye = 2;
    }

    say (name) {
        console.log('People:', name)
    }
}

class Man extends People {
    constructor() {
        super();
    }

    status () {
        console.log(this.shang, this.xia, this.eye)
    }

}

let oneMan = new Man();

oneMan.say('oneMan');

oneMan.status();

